pre.code:
on: push: branches: - master pull_request: branches: - master
name: R-CMD-check
jobs: R-CMD-check: runs-on: ${{ matrix.config.os }}
name: ${{ matrix.config.os }} (${{ matrix.config.r }})
strategy: fail-fast: false matrix: config: - {os: windows-latest, r: 'release'} - {os: macOS-latest, r: 'release'} - {os: ubuntu-20.04, r: 'release', rspm: "https://packagemanager.rstudio.com/cran/__linux__/focal/latest"} - {os: ubuntu-20.04, r: 'devel', rspm: "https://packagemanager.rstudio.com/cran/__linux__/focal/latest"}
env: R_REMOTES_NO_ERRORS_FROM_WARNINGS: true CRAN: ${{ matrix.config.cran }}
steps: - uses: actions/checkout@v1
- uses: r-lib/actions/setup-r@master with: r-version: ${{ matrix.config.r }}
- uses: r-lib/actions/setup-pandoc@master
- name: Query dependencies run: | install.packages('remotes') saveRDS(remotes::dev_package_deps(dependencies = TRUE), "depends.Rds", version = 2) shell: Rscript {0}
- name: Cache R packages if: runner.os != 'Windows' uses: actions/cache@v1 with: path: ${{ env.R_LIBS_USER }} key: ${{ runner.os }}-r-${{ matrix.config.r }}-${{ hashFiles('depends.Rds') }} restore-keys: ${{ runner.os }}-r-${{ matrix.config.r }}-
- name: Install system dependencies if: runner.os == 'Linux' env: RHUB_PLATFORM: linux-x86_64-ubuntu-gcc run: | Rscript -e "remotes::install_github('r-hub/sysreqs')" sysreqs=$(Rscript -e "cat(sysreqs::sysreq_commands('DESCRIPTION'))") sudo -s eval "$sysreqs" - name: Install dependencies run: | library(remotes) deps <- readRDS("depends.Rds") deps[["installed"]] <- vapply(deps[["package"]], remotes:::local_sha, character(1)) update(deps) remotes::install_cran("rcmdcheck") shell: Rscript {0}
- name: Check run: rcmdcheck::rcmdcheck(args = "--no-manual", error_on = "warning", check_dir = "check") shell: Rscript {0}
- name: Upload check results if: failure() uses: actions/upload-artifact@master with: name: ${{ runner.os }}-r${{ matrix.config.r }}-results path: check
- name: Test coverage if: matrix.config.os == 'macOS-latest' && matrix.config.r == 'release' run: | remotes::install_cran("covr") covr::codecov(token = "${{secrets.CODECOV_TOKEN}}") shell: Rscript {0}
- name: Build website if: matrix.config.os == 'ubuntu-20.04' && matrix.config.r == 'release' run: | sudo apt-get install libcurl4-openssl-dev libharfbuzz-dev libfribidi-dev mkdir docs git fetch origin gh-pages:gh-pages git --work-tree=docs checkout gh-pages -- . Rscript -e 'remotes::install_cran(c("pkgdown")); pkgdown::build_site()'
- name: Deploy website if: matrix.config.os == 'ubuntu-20.04' && matrix.config.r == 'release' uses: peaceiris/actions-gh-pages@v3 with: github_token: ${{ secrets.PERSO_TOKEN }} publish_dir: ./docs

myaxis <- function(side, all, main, lab = main, col1 = 1, col2 = 1, ...) {
  axis(side, at = main, labels = lab, lwd = 0, lwd.ticks = 1, col = col1, ...)
  axis(side, at = setdiff(all, main), labels = NA, lwd.ticks = .75, tck = -.025,
    col = col2, ...)
}

